package compiler.scanner;/* Scanner.java *///import org.antlr.v4.runtime.ANTLRFileStream;import org.antlr.v4.runtime.*;public class Scanner{/* Campos Scanner*//* Construtor Scanner*/	public Scanner (String archivo)throws Exception  {		Decaf lexer = new Decaf(new ANTLRFileStream(archivo));		while (lexer.nextToken().getType() != Token.EOF);	}/* Metodos Scanner */}