package compiler.scanner;/* Scanner.java *///import org.antlr.v4.runtime.ANTLRFileStream;import org.antlr.v4.runtime.*;public class Scanner{/* Campos Scanner*/	private Decaf lexer; //lexer que se puede utilizar desde la clase Parser	private String file;	/* Construtor Scanner*/	public Scanner (String archivo)throws Exception  {		lexer = new Decaf(new ANTLRFileStream(archivo));		file = new String (archivo);		//while (lexer.nextToken().getType() != Token.EOF);	}		public void Start(){		System.out.println("entrada: " + file ); // solo para debug		while (lexer.nextToken().getType() != Token.EOF);	}	public Decaf lexer(){		return lexer;	}		public String entrada(){		return file;	}/* Metodos Scanner */}